mapper.select:
select count (*)
from board_comment
where bnum = #{bnum}

select count (*)
from board
where author = #{author}

mapper.insert:
insert into board
( bnum
, title
, author
, content
, imgname
, readcnt
, pub
, regdate)
values
( board_seq.NEXTVAL
, #{title}
, #{author}
, #{content}
, #{imgName}
, 0
, #{pub}
, sysdate
)

mapper.update:
update board
set title = #{title}
, content = #{content}
, imgname = #{imgName}	
where bnum = #{bnum}
